import React, {useState, useEffect} from "react"
import Sidebar from "./components/Sidebar"
import Editor from "./components/Editor"
import { data } from "./data"
import Split from "react-split"
import {nanoid} from "nanoid"
import { array } from "yargs"

export default function App() {

// separate variable here to grab our localStorage item, there's only one key so far 

    const savedNote = localStorage.getItem("note_name")

// set the initial value to be the notes if it isn't undefined otherwise an empty array

    const [notes, setNotes] = React.useState(() => JSON.parse(savedNote) || [])
    const [currentNoteId, setCurrentNoteId] = React.useState(
        (notes[0] && notes[0].id) || ""
    )
    
// adding local storage 
// useEffect monitors changes to notes, changes made here will updated localStorage on each keystroke

    useEffect(() => {
        localStorage.setItem("note_name", JSON.stringify(notes))
        }, [notes])


    function createNewNote() {
        const newNote = {
            id: nanoid(),
            body: "# Type your markdown note's title here"
        }
        setNotes(prevNotes => [newNote, ...prevNotes])
        setCurrentNoteId(newNote.id)
    }
    
    function updateNote(text) {
        setNotes(oldNotes => {
            const newArray=[]
            for (let i=0; i++; i < oldNotes.length) { 
                if (oldNotes.id === currentNoteId) {                   
                    newArray.splice(0,0,{ ...oldNote, body: text })
                } else { 
                    newArray.push(oldNote)
                }
            }
            return newArray
        })
    }


// map will keep array items in the same index making it hard to modify

    // function updateNote(text) {
    //     setNotes(oldNotes => oldNotes.map(oldNote => {
    //         return oldNote.id === currentNoteId
    //             ? { ...oldNote, body: text }
    //             : oldNote
    //     }))
    // }
    
    function findCurrentNote() {
        return notes.find(note => {
            return note.id === currentNoteId
        }) || notes[0]
    }
    
    return (
        <main>
        {
            notes.length > 0 
            ?
            <Split 
                sizes={[30, 70]} 
                direction="horizontal" 
                className="split"
            >
                <Sidebar
                    notes={notes}
                    currentNote={findCurrentNote()}
                    setCurrentNoteId={setCurrentNoteId}
                    newNote={createNewNote}
                />
                {
                    currentNoteId && 
                    notes.length > 0 &&
                    <Editor 
                        currentNote={findCurrentNote()} 
                        updateNote={updateNote} 
                    />
                }
            </Split>
            :
            <div className="no-notes">
                <h1>You have no notes</h1>
                <button 
                    className="first-note" 
                    onClick={createNewNote}
                >
                    Create one now
                </button>
            </div>
            
        }
        </main>
    )
}
